import React from "react";
import { format } from "date-fns";
import { Link } from "react-router-dom";
import axios from 'axios';

export default function Post({ _id, title, summary, filepath, content, author, createdAt, onDelete }) {
    const imageUrl = `http://localhost:4000/blog-app/${filepath.replace(/\\/g, '/')}`;

    const handleDeleteClick = async () => {
        try {
            await axios.delete(`http://localhost:4000/upload/${_id}`, { withCredentials: true });
            console
            onDelete(_id);
        } catch (error) {
            console.error("Error deleting post:", error);
        }
    };

    return (
        <div className="post">
            <div className="image">
                <Link to={`/upload/${_id}`}>
                    <img src={imageUrl} alt="Not available" />
                </Link>
            </div>
            <div className="texts">
                <div className="title-and-delete">
                    <Link to={`/upload/${_id}`} className="link">
                        <h2>{title}</h2>
                    </Link>
                    <div className="icons">
                        <i className="far fa-trash-alt delete-icon" onClick={handleDeleteClick}></i>
                        <i className="far fa-edit edit-icon"></i>
                    </div>
                </div>
                <p className="info">
                    <a className="author">{author.username}</a>
                    <time>{format(new Date(createdAt), "MMMM d, yyyy")}</time>
                </p>
                <p className="summary">{summary}</p>
            </div>
        </div>
    );
}
