import React, { useEffect, useState } from "react";
import Post from "../components/Post";
import SearchBar from "./searchbar";
import Fuse from "fuse.js";
import axios from "axios";

export default function Homepage() {
    const [posts, setPosts] = useState([]);
    const [filteredPosts, setFilteredPosts] = useState([]); 
    const [initialLoad, setInitialLoad] = useState(true);

    useEffect(() => {
        fetchPosts();
    }, []);

    const fetchPosts = () => {
        fetch('http://localhost:4000/upload')
            .then(response => response.json())
            .then(posts => {
                setPosts(posts);
                setFilteredPosts(posts); // Set filteredPosts to all posts initially
                setInitialLoad(false);
            })
            .catch(error => {
                console.error("Error fetching posts:", error);
            });
    };

    const handleSearch = (searchKeyword) => {
        if (searchKeyword.trim() === "") {
            setFilteredPosts(posts);
            return;
        }

        const fuse = new Fuse(posts, {
            keys: ["title"],
            threshold: 0.3, 
        });

        const searchResults = fuse.search(searchKeyword);
        setFilteredPosts(searchResults.map(result => result.item));
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:4000/upload/${id}`, { withCredentials: true });

            // Remove post from state
            setPosts(prevPosts => prevPosts.filter(post => post._id !== id));
            setFilteredPosts(prevFilteredPosts => prevFilteredPosts.filter(post => post._id !== id));

            // Optionally fetch posts again to ensure the list is up-to-date
            fetchPosts();

        } catch (error) {
            console.error("Error deleting post:", error);
        }
    };

    return (
        <>
            <SearchBar onSearch={handleSearch} />

            {initialLoad
                ? <p>Loading...</p>  // Optional loading message
                : (filteredPosts.length > 0
                    ? filteredPosts.map(post => <Post key={post._id} {...post} onDelete={handleDelete} />)
                    : <p>No posts found</p> // Optional message if no posts are found
                )
            }
        </>
    );
}
