import { format} from 'date-fns';
import path from "path";

export default function Post({title,summary,filepath,content,author}) {
    const imageUrl = `http://localhost:4000/blog-app/${path.join("uploads", filepath)}`;
    return (
        <div className="post">
            <div className="image">
            <img src={imageUrl} alt="Uploaded Image" />            </div>
            <div className="texts">
                <h2>{filepath}</h2>
                <h2>{title}</h2>
                <p className="info">
                    <a className="author"></a>
                    <time>{format(new Date(), 'MMMM d, yyyy')}</time>
                </p>
                <p className="summary">{summary}</p>
            </div>
        </div>
    );

}
