import { format } from "date-fns";
export default function Post({ title, summary, filepath, content, author ,createdAt}) {
    
    const imageUrl = `http://localhost:4000/blog-app/${filepath.replace(/\\/g, '/')}`;
    
    return (
       <div className="post">
          <div className="image">
          <img src={imageUrl} alt="Not available"  />

          </div>
          <div className="texts">
             <h2>{title}</h2>
             <p className="info">
                <p className="author"></p>
                <time>{format(new Date(crea), "MMMM d, yyyy")}</time>
             </p>
             <p className="summary">{summary}</p>
          </div>
       </div>
    );
 }
