import { useState } from "react";
import "../styles/App.css";
import "../styles/createpost.css"
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";


const createpost=()=>{
    const[title,setTitle] = useState('');
    const[summary,setSummary] = useState('');
    const[content,setContent] = useState('');
    return (
        <form>
             <label for="name" className="label_tag" >Name:</label>
        <input type="text" value={title} required></input>

        
        <label for="Summary"className="label_tag">Summary:</label>
        <input type="summary" value={"summary"}required></input>
        
        <label for="Image"className="label_tag">Image:</label>
        <input type="file" required></input>
        
        <div className="text-editor"><ReactQuill value={content} /></div>
        
        <input type="submit" className="submit-button"></input>
        
        </form>
       
    );
}


export default createpost;
