 // eslint-disable-next-line 
import { format } from "date-fns";
import { Link } from "react-router-dom";
export default function Post({ title, summary, filepath, content, author ,createdAt}) {
    
    const imageUrl = `http://localhost:4000/blog-app/${filepath.replace(/\\/g, '/')}`;
    
    return (
       <div className="post">
          <div className="image">
            <Link to={'/upload/id'} >
          <img src={imageUrl} alt="Not available"  />
            </Link> 
          </div>
          <div className="texts">
            
             <h2>{title}</h2>
             <p className="info">
                <a className="author">{author.username}</a>
                <time>{format(new Date(createdAt), "MMMM d, yyyy")}</time>
             </p>
             <p className="summary">{summary}</p>
          </div>
       </div>
    );
 }
