import { useState } from "react";
import { useEffect } from "react";
import { useParams } from "react-router-dom";

export default function Searchbar()
{
    const [searchinfo,setsearchinfo] =useState(null);
   
    const {title} = useParams();
    useEffect(()=> {
         fetch(`http://localhost:4000//${title}`).then(response =>{
            response.json().then(searchinfo =>{
               setsearchinfo(searchinfo);
            })
         })
    }, []);


    if (!searchinfo) return '';

    return(
      
        <div className="post-page">
        <h1 className="h1title">{searchinfo.title}</h1>
      
        <div className="image">
          <img src={`http://localhost:4000/blog-app/${searchinfo.filepath}`}></img>
        </div>
        <div className="content" dangerouslySetInnerHTML={{__html:searchinfo.content}} />
      </div>
    )

}
