import { useContext, useEffect, } from "react";
import { Link } from "react-router-dom";
import { UserContext } from "./usercontext";
import { useNavigate } from "react-router-dom"
const jwtToken = '';

// Fetch or make a request with the JWT in the headers

export default function Header() {
const navigate = useNavigate();
const{setUserInfo,userInfo} = useContext(UserContext);

useEffect(() => {
    // Check if jwtToken is not empty or null
    if (!jwtToken) {
      console.log('JWT token is missing.');
      return;
    }
  
    // Fetch user info using JWT token
    fetch('http://localhost:4000/profile', {
      headers: {
        Authorization: `Bearer ${jwtToken}`
      }
    })
    .then(response => response.json())
    .then(userInfo => {
      setUserInfo(userInfo);
    })
    .catch(error => {
      console.log('Error fetching user info:', error);
    });
  }, []);
  


  function logout(){
    fetch('http://localhost:4000/logout',{
        credentials:'include',
        method:'POST',
    });
    setUserInfo(null);
    navigate('/');

  }
const username = userInfo?.username;
 
    return (

        <header>
            <Link to="/" className="logo">Daily Blogs</Link>
            <nav>
                {username && (
                    <>
                        <Link to="/create">Create New post</Link>
                        <a style={{cursor:"pointer"}} onClick={logout}>Logout</a>
                    </>

                )}
                {!username && (
                    <>
                        <Link to="/register">Register</Link>
                        <Link to="/login">Login</Link>

                    </>
                )}
            </nav>

        </header>


    );
}
