import { format} from 'date-fns';
import axios from 'axios';
i

export default function Post({title,summary,filePath,content,author}) {
    const [post, setPost] = useState({});

    useEffect(() => {
      axios.get('/api/posts/some-post-id')
        .then(response => {
          setPost(response.data);
        })
        .catch(error => {
          console.error('Error fetching post:', error);
        });
    }, []);
    return (
        <div className="post">
            <div className="image">
                <img src="https://images.unsplash.com/photo-1503220317375-aaad61436b1b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MTR8fHRyYXZlbHxlbnwwfHwwfHx8MA%3D%3D&w=1000&q=80"></img>
            </div>
            <div className="texts">
                <h2>{title}</h2>
                <p className="info">
                    <a className="author">{post.author?.username}</a>
                    <time>{format(new Date(), 'MMMM d, yyyy')}</time>
                </p>
                <p className="summary">{summary}</p>
            </div>
        </div>
    );

}
