import React, { useEffect, useState } from "react";
import Post from "../components/Post";
import SearchBar from "../components/SearchBar";
import stringSimilarity from "string-similarity";

export default function Homepage() {
    const [posts, setPosts] = useState([]);
    const [filteredPosts, setFilteredPosts] = useState([]); // State to hold filtered posts

    useEffect(() => {
        fetch('http://localhost:4000/upload')
            .then(response => response.json())
            .then(posts => {
                setPosts(posts);
                setFilteredPosts(posts); // Initialize filtered posts with all posts
            });
    }, []);

    const handleSearch = (searchKeyword) => {
        if (searchKeyword.trim() === "") {
            setFilteredPosts(posts);
            return;
        }

        const options = {
            includeScore: true,
            keys: ['title']
        };

        const results = stringSimilarity.findBestMatch(searchKeyword, posts, options);
        const filtered = results.ratings
            .filter(result => result.score > 0.2) // Adjust the threshold as needed
            .map(result => result.target);

        setFilteredPosts(filtered);
    };

    return (
        <>
            <SearchBar onSearch={handleSearch} />

            {filteredPosts.length > 0 && filteredPosts.map(post => (
                <Post key={post.id} {...post} />
            ))}
        </>
    );
}
