import { format} from 'date-fns';

function convertPathToUrl(path) {
    return path.replace(/\\/g, '/');
  }

export default function Post({title,summary,filepath,content,author}) {
    const filePath = convertPathToUrl(filepath);
    return (
        <div className="post">
            <div className="image">
                <img src={
                    'http://localhost:4000/'+filePath
                }></img>
            </div>
            <div className="texts">
                <h2>{title}</h2>
                <p className="info">
                    <a className="author"></a>
                    <time>{format(new Date(), 'MMMM d, yyyy')}</time>
                </p>
                <p className="summary">{summary}</p>
            </div>
        </div>
    );

}
