    console.log('Formatted filePath:', iamge);
    return (
       <div className="post">
          <div className="image">
             <img src={imageUrl} alt="Uploaded Image" />
          </div>
          <div className="texts">
             <h2>{title}</h2>
             <p className="info">
                <a className="author"></a>
                {/* <time>{format(new Date(), "MMMM d, yyyy")}</time> */}
             </p>
             <p className="summary">{summary}</p>
          </div>
       </div>
    );
 }
