import React ,{ useState } from "react";
import "../styles/App.css";
import "../styles/createpost.css"
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";

const  modules = {
    toolbar: [
      [{ 'header': [1, 2, false] }],
      ['bold', 'italic', 'underline','strike', 'blockquote'],
      [{'list': 'ordered'}, {'list': 'bullet'}, {'indent': '-1'}, {'indent': '+1'}],
      ['link', 'image'],
      ['clean']
    ],
  };

const formats = [
  'header',
  'bold', 'italic', 'underline', 'strike', 'blockquote',
  'list', 'bullet', 'indent',
  'link', 'image'
];

const Createpost=()=>{
    const [title,setTitle] = useState('');
    const [summary,setSummary] = useState('');
    const[content,setContent] = useState('');

    function createnewpost(e){
        e.preventDefault();
        fetch('http://localhost:4000/post',{
            methdi
        
        })
    }

    return (
        <form onSubmit={createnewpost}>
             <label for="name" className="label_tag" >Name:</label>
        <input type="text" value={title} onChange={e=>setTitle(e.target.value)} required></input>

        
        <label for="Summary"className="label_tag">Summary:</label>
        <input type="summary" value={summary} onChange={e=>setSummary(e.target.value)}required></input>
        
        <label for="Image"className="label_tag">Image:</label>
        <input type="file" required></input>
        
        <div className="text-editor"><ReactQuill value={content} onChange={newvalue=>setContent(newvalue)}modules={modules} formats={formats} /></div>
        
        <input type="submit" className="submit-button"></input>
        
        </form>
       
    );
}


export default Createpost;
